package com.example.RolCharacterBook.model;

import android.util.Log;

import java.text.ParseException;
import java.text.SimpleDateFormat;
import java.util.ArrayList;
import java.util.Date;
import java.util.List;
import java.util.UUID;

import io.realm.Realm;
import io.realm.RealmQuery;

public class Data {
    private static final Data DATA = new Data();
    private Character actual;
    private ArrayList<Character> items;


    private Data() {
        this.items = new ArrayList();
        Realm realm = Realm.getDefaultInstance();

        //Para cargar los datos de ejemplo usamos una clase que guardaremos en la base de datos, una vez que exista la instancia de la clase ya no los cargara mas, para volver a
        //insertarlos borramos la base de datos
        final Example e = realm.where(Example.class).equalTo("id", 1).findFirst();
        if (e == null) {

            items.add(new Character(UUID.randomUUID().toString(), "Acut", "aangserrano@iesfranciscodelosrios.es", ""));
            items.add(new Character(UUID.randomUUID().toString(), "Relen", "aangserrano@iesfranciscodelosrios.es", ""));
            items.add(new Character(UUID.randomUUID().toString(), "Helena", "aangserrano@iesfranciscodelosrios.es", ""));
            items.add(new Character(UUID.randomUUID().toString(), "Saulok", "aangserrano@iesfranciscodelosrios.es", ""));
            items.add(new Character(UUID.randomUUID().toString(), "Aless", "aangserrano@iesfranciscodelosrios.es", ""));
            items.add(new Character(UUID.randomUUID().toString(), "Hombre desconocido", "creador@example.com", null));
            items.add(new Character(UUID.randomUUID().toString(), "Lobo desconocido", "creador@example.com", null));
            items.add(new Character(UUID.randomUUID().toString(), "Sucubo desconocida", "creador@example.com", null));
            items.add(new Character(UUID.randomUUID().toString(), "Hezrou desconocido", "creador@example.com", null));
            items.add(new Character(UUID.randomUUID().toString(), "Bebelith desconocida", "creador@example.com", null));

            //clase unica para el ejemplo
            saveClass("Nothing");
            for (Character c : items) {
                Log.d(c.getName(), c.getId());
                c.setCharClass("Nothing");
                realm.executeTransaction(r -> {
                    realm.copyToRealmOrUpdate(c);
                });
            }
            realm.executeTransaction(r -> {
                realm.copyToRealmOrUpdate(new Example()); //creamos la instancia de Example para no cargar mas los datos de ejemplo
            });
        }
    }

    public static Data getDATA() {
        return DATA;
    }

    public Character getActual() {
        return actual;
    }

    public void setActual(Character actual) {
        this.actual = actual;
    }

    public ArrayList<Character> getItems() {
        return items;
    }

    public boolean remove(Character c) {
        Realm realm = Realm.getDefaultInstance();

        realm.executeTransaction(r -> {
            realm.where(Character.class).equalTo("UUID", c.getId()).findFirst().deleteFromRealm();
        });
        boolean remove = items.remove(c);

        realm.close();
        return remove;
    }

    public boolean add(Character c) {
        return items.add(c);
    }

    public void save(Character c) {

        Realm realm = Realm.getDefaultInstance();

        realm.executeTransaction(r -> {
            realm.copyToRealmOrUpdate(c);
            if (!items.contains(c)) {
                items.add(c);
            }
        });

        realm.close();
    }

    public ArrayList<Character> loadAll() {

        ArrayList<Character> allChar = new ArrayList<>();
        ArrayList<Character> usedList = new ArrayList<>();
        Realm realm = Realm.getDefaultInstance();

        allChar = new ArrayList<>(realm.where(Character.class).findAll());

        for (Character c : allChar) {
            Character aux = new Character();
            aux.setEmail(c.getEmail());
            aux.setName(c.getName());
            aux.setId(c.getId());
            aux.setPortrait(c.getPortrait());
            usedList.add(aux);
        }

        realm.close();
        this.items = usedList;
        return usedList;
    }

    public Character getById(String id) {
        Character c = null;

        Realm realm = Realm.getDefaultInstance();

        c = realm.where(Character.class)
                .equalTo("UUID", id)
                .findFirst();
        this.actual = new Character(c.getName(), c.getEmail(), c.getCharClass(), c.getStrength(), c.getDexterity(), c.getConstitution(), c.getIntelligence(),
                c.getWisdom(), c.getCharisma(), c.getPlayer(), c.getPlayDate(), c.getPortrait(), c.getId());
        realm.close();
        return this.actual;
    }


    public ArrayList<String> loadClass() {
        Realm realm = Realm.getDefaultInstance();
        ArrayList<String> s = new ArrayList<>();
        ArrayList<ClassString> c = new ArrayList<>(realm.where(ClassString.class).findAll());
        for (ClassString aux : c) {
            s.add(aux._name);
        }
        return s;
    }

    public void saveClass(String s) {
        ClassString c = new ClassString();
        c._name = s;
        Realm realm = Realm.getDefaultInstance();

        realm.executeTransaction(r -> {
            realm.copyToRealmOrUpdate(c);
        });

        realm.close();
    }

    public List<Character> search(String name, String date, String charClass) {
        ArrayList<Character> list = new ArrayList<>();
        Realm realm = Realm.getDefaultInstance();
        RealmQuery query = realm.where(Character.class);
        if (name != null && !name.equals("")) {
            query = query.like("name", "*" + name + "*");
        }
        if (date != null && !date.equals("")) {
            try {
                Date datesearch = new SimpleDateFormat("yyyy-MM-dd").parse(date);
                datesearch.setHours(0);
                datesearch.setMinutes(0);
                datesearch.setSeconds(0);
                Date end = new SimpleDateFormat("yyyy-MM-dd").parse(date);
                end.setHours(23);
                end.setMinutes(59);
                end.setSeconds(59);
                query = query.greaterThanOrEqualTo("playDate", datesearch).lessThanOrEqualTo("playDate", end);
            } catch (ParseException e) {
                e.printStackTrace();
            }
        }

        if (charClass != null && !charClass.equals("")) {
            query = query.equalTo("charClass", charClass);
        }
        list = new ArrayList(query.findAll());

        for (Character c : list) {
            Log.d("Buscado", "search: " + c.getName());
        }
        this.items = list;
        return list;
    }

}
